use crate::providers::upcloud::UpCloudProvider;
use crate::providers::MetadataProvider;
use mockito;

#[test]
fn test_hostname() {
    let ep = "/metadata/v1/hostname";
    let hostname = "test-hostname";

    let mut server = mockito::Server::new();
    let mut provider = UpCloudProvider::try_new().unwrap();
    provider.client = provider.client.max_retries(0).mock_base_url(server.url());

    server.mock("GET", ep).with_status(503).create();
    provider.hostname().unwrap_err();

    server
        .mock("GET", ep)
        .with_status(200)
        .with_body(hostname)
        .create();
    let v = provider.hostname().unwrap();
    assert_eq!(v, Some(hostname.to_string()));

    server.mock("GET", ep).with_status(404).create();
    let v = provider.hostname().unwrap();
    assert_eq!(v, None);

    server
        .mock("GET", ep)
        .with_status(200)
        .with_body("")
        .create();
    let v = provider.hostname().unwrap();
    assert_eq!(v, None);

    server.reset();
    provider.hostname().unwrap_err();
}

#[test]
fn test_pubkeys() {
    let mut server = mockito::Server::new();
    let mut provider = UpCloudProvider::try_new().unwrap();
    provider.client = provider.client.max_retries(0).mock_base_url(server.url());

    server.mock("GET", "/metadata/v1/public_keys")
        .with_status(200)
        .with_body("ssh-rsa  root@example1\n
                   ssh-rsa  root@example2")
    .create();

    let keys = provider.ssh_keys().unwrap();
    assert_ne!(keys, vec![]);
    assert_eq!(keys.len(), 2);

    assert_eq!(keys[0].options, None);
    assert_eq!(keys[0].comment, Some("root@example1".to_string()));

    assert_eq!(keys[1].options, None);
    assert_eq!(keys[1].comment, Some("root@example2".to_string()));

    server.reset();
    provider.ssh_keys().unwrap_err();
}

#[test]
fn test_attributes() {
    let instance_id = "test-instance-id";
    let hostname = "test-hostname";
    let region = "test-region";

    let endpoints = maplit::btreemap! {
        "/metadata/v1/hostname" => hostname,
        "/metadata/v1/instance_id" => instance_id,
        "/metadata/v1/region" => region,
    };

    let mut server = mockito::Server::new();
    for (endpoint, body) in endpoints {
        server
            .mock("GET", endpoint)
            .with_status(200)
            .with_body(body)
            .create();
    }

    let attributes = maplit::hashmap! {
        "UPCLOUD_INSTANCE_ID".to_string() => instance_id.to_string(),
        "UPCLOUD_HOSTNAME".to_string() => hostname.to_string(),
        "UPCLOUD_REGION".to_string() => region.to_string(),
    };

    let client = crate::retry::Client::try_new()
        .unwrap()
        .max_retries(0)
        .return_on_404(true)
        .mock_base_url(server.url());
    let provider = UpCloudProvider { client };

    let v = provider.attributes().unwrap();
    assert_eq!(v, attributes);

    server.reset();
    provider.attributes().unwrap_err();
}
